// with_markdown.min.js
var markdown_opened = 1;
function close_markdown() {
  const t =
      "open Markdown" === $("#switch-comment").attr("title")
        ? "close Markdown"
        : "open Markdown",
    e = $("#switch-comment>i");
  !markdown_opened
    ? $("#vcomment").slideDown("normal", () => {
        $("#switch-comment").attr("title", t),
          (markdown_opened = 1),
          e.removeClass("far fa-edit").addClass("fas fa-edit");
      })
    : $("#vcomment").slideUp("normal", () => {
        $("#switch-comment").attr("title", t),
          (markdown_opened = 0),
          e.removeClass("fas fa-edit").addClass("far fa-edit");
      });
}

function open_comments() {
  !markdown_opened
    ? $("#post-comment").slideUp("normal", () => {
      })
    : $("#post-comment").slideDown("normal", () => {
      });
}

var commentElement = document.getElementsByClassName("comment-head-md")[0];
commentElement.innerHTML +=
  '</br>&emsp;<b <i class="far fa-comments"></i>&nbsp;with&nbsp;<i class="fab fa-markdown"></i>&nbsp;Markdown? &nbsp;<i class="far fa-hand-point-right"></i>&nbsp;</b>';
null != commentElement &&
  ((commentElement.innerHTML +=
  //   '<a id="switch-comment" href="javascript:void(0);" title="open Markdown" target="_self"> <i class="far fa-edit" aria-hidden="true"></i></a>'),
  // (document.getElementById("vcomment").style.display = "none")
  '<a id="switch-comment" href="javascript:void(0);" title="close Markdown" target="_self"> <i class="fas fa-edit" aria-hidden="true"></i></a>'),
  (document.getElementById("post-comment").style.display = "none")
  ),
  $("#switch-comment").click(function () {
    return close_markdown(), open_comments(), !1;
  });
